            parentRec = me.getParentRecord(),
            dirArrow = me.getDirectionArrow(),
            selectMsg = 'Please choose 1 {0} row',
            unsavedMsg = 'Please save the {0} row',
            directionMsg = '<div class="arrow-{0}-mask">{1}</div>',
