                var description;
                try {
                    description = ' "' + Ext.decode(savedQueue[0]).Properties.Description + '"' + (this.panelName ? ' in "' + this.panelName + '"' : '');
                } catch (e) {
                    description = ' "' + this.panelName || '';
                }
